class App extends Component {
  state = {
    post: {},
  };

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/todos/1")
      .then((response) => {
        return response.json();
      })
      .then((result) => {
        // set time out pour simuler chargement👍🏻
        setTimeout(() => {}, 15);
        this.setState({ post: result });
      });
  }
  render() {
    return (
      <div>
        <h1>Notre premier article:</h1>
      </div>
    );
  }
}

export default App;
